<div class="body"><!-- ALL USERS -->	<?php if ($act == 'all'):?>	<h1 class="title"><?=$h1?></h1>		<div class="nav">		<div class="fleft"></div>		<div class="fright">			<a class="button green" href="<?=$path?>?add">Добавить карту</a>		</div>	</div>		<table class="table-1" data-scroll="head">		<thead>			<tr>				<td class="small">Code</td>				<td class="">Скидка (%)</td>				<td class="small"></td>				<td class="small"></td>			</tr>		</thead>		<tbody>		<?php foreach ($carts as $k):?>			<tr>				<td class="left"><b><?=$k->code?></b></td>				<td class="left"><?=$k->percent?></td>				<td>					<a class="link_edit" title="Редактировать карту" href="<?=$path?>?edit=<?=$k->code?>"></a>				</td>				<td>					<a class="link_del" data-delete="карту <b><?=htmlspecialchars($k->code)?></b>" href="<?=$path?>?delete=<?=$k->code?>" title="удалить карту"></a>				</td>			</tr>		<?php endforeach;?>		</tbody>	</table>	<?php endif;?>			<?php if ($act == 'add'):?>	<h1 class="title"><?=$h1?></h1>		<div class="nav">		<div class="fleft"></div>		<div class="fright">			<a class="button blue" data-form-apply="">Сохранить</a>			<a class="button black" href="<?=$path?>">Отмена</a>		</div>	</div>		<form id="form" action="<?=$path?>" method="POST" enctype="multipart/form-data">		<div class="toggle-box">			<a class="bookmark-toggle activ" data-name="data" href="#data">Общие</a>		</div>				<div class="bookmark activ" data-id="data">			<table class="table-1">				<tr>					<td class="small right"><b>Код:</b></td>					<td class="left">						<input class="inf" type="text" name="code" value="">					</td>				</tr>				<tr>					<td class="right nowrap"><b>Скидка (%):</b></td>					<td class="left">						<input class="inf" data-mask="price" type="text" name="percent" value="">					</td>				</tr>			</table>		</div>				<input type="hidden" name="add" value="">	</form>	<?php endif;?>				<?php if ($act == 'edit'):?>	<h1 class="title"><?=$h1?></h1>		<div class="nav">		<div class="fleft"></div>		<div class="fright">			<a class="button blue" data-form-apply="">Сохранить</a>			<a class="button black" href="<?=$path?>">Отмена</a>		</div>	</div>		<form id="form" action="<?=$path?>" method="POST" enctype="multipart/form-data">		<div class="toggle-box">			<a class="bookmark-toggle activ" data-name="data" href="#data">Общие</a>		</div>				<div class="bookmark activ" data-id="data">			<table class="table-1">				<tr>					<td class="small right"><b>Код:</b></td>					<td class="left"><?=$cart->code?></td>				</tr>				<tr>					<td class="right nowrap"><b>Скидка (%):</b></td>					<td class="left">						<input class="inf" data-mask="price" type="text" name="percent" value="<?=$cart->percent?>">					</td>				</tr>			</table>		</div>				<input type="hidden" name="code" value="<?=$cart->code?>">		<input type="hidden" name="edit" value="">	</form>	<?php endif;?>	</div><!-- END BODY --><script> // APPLY$(document).on('click', '[data-form-apply]', function(e){	var l = $('<div class="load"></div>').appendTo(document.body);		AP.init('', $('#form')[0], function(data){		if ( ! data.response.length){			location.href = '/admin/carts';		}else{			var res = $('<ol></ol>');			$.each(data.response, function(){				res.append('<li>'+this+'</li>');			});						res.dialog({				height:'auto',				width:600,				drag: true,				title:'ОШИБКА'			});						l.remove();		}	}, 'json');		return false;		});</script>