<div class="body">	<h1 class="title"><?=$h1?></h1>		<div class="nav">		<div class="fleft"></div>		<div class="fright">			<a class="button orange" data-bind="sendMail">Разослать письма</a>		</div>	</div>		<form id="form" action="<?=$path?>" method="POST" enctype="multipart/form-data">		<div class="toggle-box">			<a class="bookmark-toggle activ" data-name="data" href="#data">Общие</a>		</div>		<div class="bookmark activ" data-id="data">			<table class="table-1">				<tbody>					<tr>						<td class="small right"><b>Тема</b></td>						<td class="">							<input class="inf" type="text" name="tema" value="">						</td>					</tr>					<tr>						<td class="right"><b>Сообщение</b></td>						<td>							<textarea class="tiny" style="height:500px;" name="message"></textarea>						</td>					</tr>				</tbody>			</table>		</div>		<input type="hidden" name="send_newsletter" value="">	</form>	</div><!-- END BODY --><script> // SEND MAIL$(document).on('click', '[data-bind="sendMail"]', function(e){	e.preventDefault();		var confirm = $('<div class="fm-confirm">'+						'<div>Разослать письма?</div>'+						'<br>'+						'<div class="fm-buttons-box">'+							'<button class="fm-buttons white" data-confirm="cancel">Отмена</button>'+							'<button class="fm-buttons green" data-confirm="agree">OK</button>'+						'</div>'+					'</div>');	confirm.dialog({		width:'300px',		height:'auto',		title:'Подтверждение',		drag:true	});		confirm.data('dialog').dialog.find('[data-confirm="cancel"]').click(function(e){		confirm.dialog('close');	});		confirm.data('dialog').dialog.find('[data-confirm="agree"]').click(function(e){		confirm.dialog('close');				$(document.body).append($('<div class="load"></div>'));		if (tinyMCE && tinyMCE.editors){			$(tinyMCE.editors).each(function(){				$(this.getElement()).html(this.getContent());			});		}		AP.init('', $('#form')[0], function(){				location.reload(true);		});	});});</script>