<?php $i=1; foreach ($invoices as $k):?><tr>	<td><?=$i++?></td>	<td class="left">		<?=date('d m Y :: h:m:s', $k->date)?>	</td>	<td class="left nowrap">		<?=$k->type?>	</td>	<td>		<a class="link_edit" data-bind="edit_product" data-id="<?=$k->id?>" href="" title="редактировать"></a>	</td>	<td>		<a class="link_del" data-bind="delete_product" data-name="<?=$k->type .'::'. date('d m Y :: h:m:s', $k->date)?>" data-id="<?=$k->id?>" href="" title="удалить"></a>	</td></tr><?php endforeach;?>