<?php $i=1; foreach ($products as $k):?><tr>	<td><?=$i++?></td>	<td>		<span class="icon-reorder handler" data-sortable="handler" title="перемеcтить"></span>		<input data-sortable="id" type="hidden" name="product_id[]" value="<?=$k->id?>">		<input data-sortable="order" type="hidden" name="product_order[]" value="<?=$k->order?>">	</td>	<td class="left">		<?=$k->name?>	</td>	<td class="left nowrap">		<?=$k->serial_number?>	</td>	<td>		<a class="link_edit" data-bind="edit_product" data-id="<?=$k->id?>" href="" title="редактировать"></a>	</td>	<td>		<a class="link_del" data-bind="delete_product" data-name="<?=htmlspecialchars($k->name)?>" data-id="<?=$k->id?>" href="" title="удалить"></a>	</td></tr><?php endforeach;?>