<?php $i=1; foreach ($products as $k):?><tr>	<td><?=$i++?></td>	<td>		<span class="icon-reorder handler" data-sortable="handler" title="перемеcтить"></span>		<input data-sortable="id" type="hidden" name="product_id[]" value="<?=$k->id?>">		<input data-sortable="order" type="hidden" name="product_order[]" value="<?=$k->order?>">	</td>	<td class="left">		<?=$k->name?>	</td>	<td class="left nowrap">		<?=$k->serial_number?>	</td>	<td>		<a class="link_edit" href="<?=$path?>?parent=<?=$parent?>&update=<?=$k->id?>" title="редактировать"></a>	</td>	<td>		<a class="link_del" data-delete="<?=htmlspecialchars($k->name)?>" href="<?=$path?>?parent=<?=$parent?>&delete=<?=$k->id?>"  title="удалить"></a>	</td></tr><?php endforeach;?>