<?php $i=1; foreach ($sizes as $k):?><tr data-size-item="item">	<td><?=$i++;?></td>	<td>		<span class="icon-reorder handler" data-sortable="handler" title="перемеcтить"></span>		<input type="hidden" data-sortable="id" name="size_id[]" value="<?=$k->id?>">		<input type="hidden" data-sortable="order" name="size_order[]" value="<?=$k->order?>">	</td>	<td class="left">		<?=$k->name?>	</td>	<td class="left">		<?=$k->prefix?>	</td>	<td>		<a class="link_edit" data-bind="edit_size" data-id="<?=$k->id?>" href=""></a>	</td>	<td>		<a class="link_del" data-bind="delete_size" data-name="<?=htmlspecialchars($k->name)?>" data-id="<?=$k->id?>" title="удалить"></a>	</td></tr><?php endforeach;?>