<?php $i=1; foreach ($suppliers as $k):?><tr>	<td><?=$i++?></td>	<td>		<span class="icon-reorder handler" data-sortable="handler" title="перемеcтить"></span>		<input data-sortable="id" type="hidden" name="supplier_id[]" value="<?=$k->id?>">		<input data-sortable="order" type="hidden" name="supplier_order[]" value="<?=$k->order?>">	</td>	<td class="left">		<a data-bind="getSupplier" data-id="<?=$k->id?>" href="" title="Смотреть"><?=$k->name?></a>	</td>	<td class="left">		<?=$k->person?>	</td>	<td class="left nowrap">		<?=$k->phone?>	</td>	<td class="left">		<?=$k->email?>	</td>	<td class="left">		<?=$k->website?>	</td>	<td class="left nowrap">		<?=$k->payment_details?>	</td>	<td>		<a class="link_edit" data-bind="edit_supplier" data-id="<?=$k->id?>" href="" title="редактировать"></a>	</td>	<td>		<a class="link_del" data-bind="delete_supplier" data-name="<?=htmlspecialchars($k->name)?>" data-id="<?=$k->id?>" href="" title="удалить"></a>	</td></tr><?php endforeach;?>